mapper.select:
SELECT * FROM cart_detail
join book on cart_detail.book_book_seq = book.book_seq
where customer_customer_seq = #{customer_seq}

mapper.delete:
DELETE FROM cart_detail
WHERE customer_customer_seq = #{customer_seq}
and book_book_seq = #{book_seq}

