mapper.select:
select * from customer
join membership on membership.membership_num = customer.membership_membership_num
where customer_email = #{customer_email}
and customer_password = #{customer_password}

mapper.delete:
delete from address
where address_seq = #{address_seq}
and customer_customer_seq = #{customer_seq}

delete from card
where card_seq = #{card_seq}
and customer_customer_seq = #{customer_seq}
